import java.sql.*;

public class TestFarmerDetails {
    public static void main(String[] args) {
        String url = "jdbc:mysql://localhost:3306/efarm_db";
        String user = "root";
        String password = "";

        System.out.println("Testing farmer_details table...\n");

        try {
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection conn = DriverManager.getConnection(url, user, password);

            // Query the farmer_details table
            String query = "SELECT id, username, location, farm_size, contact_number, email FROM farmer_details";
            Statement stmt = conn.createStatement();
            ResultSet rs = stmt.executeQuery(query);

            System.out.println("farmer_details Table Contents:");
            System.out.println("=".repeat(80));
            System.out.printf("%-5s %-15s %-20s %-10s %-15s %-25s%n", 
                "ID", "Username", "Location", "Farm Size", "Contact", "Email");
            System.out.println("-".repeat(80));

            int count = 0;
            while (rs.next()) {
                count++;
                System.out.printf("%-5d %-15s %-20s %-10.2f %-15s %-25s%n",
                    rs.getInt("id"),
                    rs.getString("username"),
                    rs.getString("location"),
                    rs.getDouble("farm_size"),
                    rs.getString("contact_number"),
                    rs.getString("email")
                );
            }

            System.out.println("=".repeat(80));
            System.out.println("\n[SUCCESS] Found " + count + " record(s) in farmer_details table.");
            System.out.println("[OK] Table 'farmer_details' exists and is accessible!");

            rs.close();
            stmt.close();
            conn.close();

        } catch (SQLException e) {
            System.out.println("[ERROR] Database error: " + e.getMessage());
            e.printStackTrace();
        } catch (ClassNotFoundException e) {
            System.out.println("[ERROR] MySQL driver not found!");
            e.printStackTrace();
        }
    }
}

